create table deployments
(
    id                     uuid                     not null
        primary key,
    client_id              uuid,
    client_name            varchar(200),
    product_id             uuid                     not null,
    product_name           varchar(200)             not null,
    status                 enum_deployments_status          default 'Not Started'::enum_deployments_status,
    deployment_type        enum_deployments_deployment_type default 'ga'::enum_deployments_deployment_type,
    environment            enum_deployments_environment,
    next_delivery_date     date,
    feature_name           varchar(200),
    release_items          text,
    delivery_person        varchar(100),
    notes                  text,
    documentation          jsonb                            default '{"runbook": null, "qaReport": null, "releaseNotesLink": null}'::jsonb,
    relevant_docs          varchar(255)[]                   default (ARRAY []::character varying[])::character varying(255)[],
    equipment_s_a_status   varchar(20)                      default 'not_started'::character varying,
    equipment_s_e_status   varchar(20)                      default 'not_started'::character varying,
    mapping_status         varchar(20)                      default 'not_started'::character varying,
    construction_status    varchar(20)                      default 'not_started'::character varying,
    blocked_comments       jsonb                            default '[]'::jsonb,
    status_history         jsonb                            default '[]'::jsonb,
    created_at             timestamp with time zone not null,
    updated_at             timestamp with time zone not null,
    deleted_at             timestamp with time zone,
    client_ids             uuid[]                           default ARRAY []::uuid[],
    client_names           varchar(255)[]                   default (ARRAY []::character varying[])::character varying(255)[],
    owner_id               uuid,
    owner_name             varchar(100),
    notification_emails    varchar(255)[]                   default (ARRAY []::character varying[])::character varying(255)[],
    last_notification_sent jsonb                            default '{}'::jsonb
);

alter table deployments
    owner to db1usr;

