create table products
(
    id                  uuid                     not null
        primary key,
    name                varchar(200)             not null,
    description         text,
    product_owner       varchar(100),
    engineering_owner   varchar(100),
    next_release_date   date,
    parent_id           uuid,
    parent_name         varchar(200),
    documentation       jsonb          default '{"testCases": null, "demoScript": null, "productGuide": null, "releaseNotes": null, "productionChecklist": null}'::jsonb,
    relevant_docs       jsonb          default '{"testCases": true, "demoScript": true, "productGuide": true, "releaseNotes": true, "productionChecklist": true}'::jsonb,
    eap                 jsonb          default '{"endDate": null, "isActive": false, "clientIds": [], "startDate": null, "jiraBoardUrl": null}'::jsonb,
    is_adapter          boolean        default false,
    adapter_services    jsonb          default '{"hasEquipmentSA": false, "hasEquipmentSE": false, "hasMappingService": false, "hasConstructionService": false}'::jsonb,
    notification_emails varchar(255)[] default (ARRAY []::character varying[])::character varying(255)[],
    created_at          timestamp with time zone not null,
    updated_at          timestamp with time zone not null,
    deleted_at          timestamp with time zone,
    delivery_lead       varchar(100)
);

alter table products
    owner to db1usr;

