create table users
(
    id                       uuid                     not null
        primary key,
    email                    varchar(255)             not null
        constraint users_email_key7
            unique
        constraint users_email_key2
            unique
        unique
        constraint users_email_key1
            unique
        constraint users_email_key3
            unique
        constraint users_email_key4
            unique
        constraint users_email_key5
            unique
        constraint users_email_key6
            unique
        constraint users_email_key8
            unique,
    password_hash            varchar(255)             not null,
    name                     varchar(100)             not null,
    role                     enum_users_role default 'user'::enum_users_role,
    assigned_product_ids     uuid[]          default ARRAY []::uuid[],
    managed_team_ids         uuid[]          default ARRAY []::uuid[],
    max_capacity             integer         default 5,
    skills                   varchar(255)[]  default (ARRAY []::character varying[])::character varying(255)[],
    notification_preferences jsonb           default '{"email": true, "inApp": true, "events": []}'::jsonb,
    last_login_at            timestamp with time zone,
    created_at               timestamp with time zone not null,
    updated_at               timestamp with time zone not null,
    deleted_at               timestamp with time zone
);

alter table users
    owner to db1usr;

